import React from 'react'
import CategoryNav from './CategoryNav'
import { NavLink } from 'react-router-dom'
import Categoryitems from './CategoryApi'
const Categoryshow = () => {
  const [items,setitems]=useState(Categoryitems)
  const it=Categoryitems.filter((ele)=>{
    return ele.category;
  })
  console.log(items)
  return (
    <>
      <CategoryNav />

      <div className='firstdiv'>
        <div className='categoryflex'>
          <img className='firstimg' src='images/backG4BG.png' alt='not found' />
          <div style={{ display: "flex", flexDirection: "column", marginLeft: "20px" }}>
            <h1 style={{ fontSize: "40px", marginTop: "300px" }}>High On Trend, <span style={{ fontFamily: "PT Sans Narrow, sans-serif", textDecoration: "underline" }}>Low on Costs</span> </h1>
            <NavLink style={{ fontSize: "19px", color: "black", margin: "10px" }} to='/'>Shop &rarr;</NavLink>
          </div>
        </div>
      </div>

      <div className="container">
        {
          Categoryitems.map((ele)=>{
            return  <div className='catediv'>
            <img className="categoryimg" src={ele.img} alt="not found" />
            <h4 className="categoryh4">Zara Design Women Collection</h4>
            <h4 className="price"> <span className="prevprice">&#8377;4299</span> &#8377;3999 <span
              className="discount">30%</span><span><button className="cartadd">Add to cart</button></span>
            </h4>
          </div>
          })
        }
        </div>


    </>
  )
}

export default Categoryshow
