import App from '../App';

const storetoken=createContext();

const Apicontext = ({child}) => {

    const tokenstoreftn=(Token)=>{
        localStorage.setItem('token',Token)
    }

  return (
    <>
      <storetoken.Provider tokenstoreftn={tokenstoreftn}>
      </storetoken.Provider>
    </>
  )
}

export {Apicontext,storetoken}
