import React, { useState } from 'react'
import MainPage from './components/MainPage'
import ItemsShow from './components/ItemShow'
import Login from './components/Login';
import { Routes, Route } from "react-router-dom";
import Signup from './components/Signup';
import items from './components/Api';

function App() {
  const [cat, setcat] = useState(items)

  const categoryfilter = (category) => {
    const filtercategory = items.filter((ele) => {
      return ele.category === category
    })
    setcat(filtercategory)
  }

  return (
    <>
      <Routes>
        <Route path="/" element={<MainPage categoryfilter={categoryfilter} />} />

        <Route path="women" key={'women'} element={<ItemsShow categoryfilter={categoryfilter} cate={cat} />} />
        <Route path="men" key={'men'} element={<ItemsShow categoryfilter={categoryfilter} cate={cat} />} />
        <Route path="kids" key={'kids'} element={<ItemsShow categoryfilter={categoryfilter} cate={cat} />} />
        <Route path="perfume" key={'perfume'} element={<ItemsShow categoryfilter={categoryfilter} cate={cat} />} />

        {/* <Route path="contact" key={'contact'} element={<ItemShow />} /> */}

        <Route path="login" key={'login'} element={<Login />} />
        <Route path="signup" key={'signup'} element={<Signup />} />

        {/* <Route path="*" element={<ItemShow />} /> */}
      </Routes>
    </>
  );
}

export default App;
