import React, { useState } from 'react'
import { NavLink } from 'react-router-dom'

const Login = () => {
  const [info, setinfo] = useState({
    email: "",
    password: ""
  })

  const submitform = (event) => {
    event.preventDefault()
    console.log("Form is sumbitted")
  }

  const changevalue=(event)=>{
    let name=event.target.name;
    let value=event.target.value;

    setinfo({
      ...info,
      [name]:value
    })
  }
  const submitbtn=()=>{
    console.log("SUBMIT BTN CLICK")
    console.log("INFO: ",info)
  }
  return (
    <>
      <div className="loginouterdiv">

        <div className="logindiv">

          <div className="divgreen">
          </div>
          <div className='formdiv'>
            <h1 className="loginzara">MD's Fashion - login</h1>

            <form action="" className="logininfo" onSubmit={submitform} >
              <h3>Email</h3>

              <input type="email" onChange={changevalue} value={info} name='email' placeholder="Email  " required />

              <h3>Password</h3>

              <input type="password" onChange={changevalue} name='password' placeholder="Password  " required />

              <input className="submit" type="submit" value="Login" onClick={submitbtn} />

              <h4 style={{ textAlign: "center" }}>Don`t have account <NavLink to="/signup">Sign up</NavLink></h4>
            </form>

          </div>


        </div>

      </div>
    </>
  )
}

export default Login
